mapper.select:
select
	sa.author_id as authorId,
	sa.create_date as createDate,
	sa.disabled,
	sa.id,
	sa.maxlat,
	sa.maxlng,
	sa.minlat,
	sa.minlng
from
	t_studyarea sa

	and sa.author_id = #{authorId}
	and sa.create_date = #{createDate}	
	and sa.disabled = #{disabled}
	and sa.id = #{id}
	and sa.maxlat = #{maxlat}
	and sa.maxlng = #{maxlng}
	and sa.minlat = #{minlat}
	and sa.minlng = #{minlng}

mapper.update:
update
	t_studyarea
set
	disabled = #{disabled}
where
	author_id = #{authorId}
